import uvicorn
from fastapi import FastAPI, HTTPException
from pydantic import BaseModel, Field
import uuid
from agent import initialize_agent, get_non_stream_response
from answer_types import AnswerType, ChatAnswer, RawPieChartAnswer
from typing import Optional
from enum import Enum

from fastapi.middleware.cors import CORSMiddleware

app = FastAPI(docs_url="/", redoc_url=None)

# Specify the origins you want to allow
app.add_middleware(
    CORSMiddleware,
    allow_origins=["*"],  # Allow requests from any origin
    allow_credentials=True,
    allow_methods=["*"],
    allow_headers=["*"],
)


agent_executor = initialize_agent()

# In-memory conversation storage (for testing)
conversation_threads = {}


def is_valid_uuid(value: str):
    try:
        uuid_obj = uuid.UUID(value, version=4)  # Change version as needed
        return str(uuid_obj) == value.lower()  # Ensure exact match
    except ValueError:
        return False


class ChartReturnType(str, Enum):
    RENDERED = "rendered"
    NOT_RENDERED = "no_rendered"


# Model request schema
class ChatRequest(BaseModel):
    thread_id: uuid.UUID = Field(
        ...,
        description="A UUID string for the conversation thread. Users can make arbitrary thread IDs.",
    )
    message: str = Field(
        ...,
        description="The message for the agent.",
    )
    sender_wallet: Optional[str] = Field(
        default=None,
        description="The message for the agent.",
    )

    chart_return_type: Optional[ChartReturnType] = Field(
        ChartReturnType.RENDERED,
        description="Whether charts should be returned as raw chart data, or as a rendered image.",
    )


@app.post("/chat")
async def chat(request: ChatRequest) -> list[ChatAnswer]:
    """Handle chat messages and maintain session memory.

    The user provides a message and a thread ID. The agent processes the message,
    and the response is returned. The thread ID is used to maintain session memory.

    Args:
        request (ChatRequest): The request body containing the message, thread ID, and sender wallet.

    Returns:
        list[ChatAnswer]: A list of ChatAnswer objects containing the response.
    """
    # Check that the thread ID is a valid UUID.
    if not is_valid_uuid(str(request.thread_id)):
        raise HTTPException(status_code=400, detail="Thread ID is not a valid UUID.")

    # Get the user's message and append their wallet address to the message if provided.
    user_input = request.message
    if (
        request.sender_wallet is not None
        and request.sender_wallet != ""
        and request.sender_wallet != "0x"
    ):
        user_input += f"My wallet address is {request.sender_wallet}"

    # Get the response from the agent.
    answers: list[ChatAnswer] = get_non_stream_response(
        agent_executor=agent_executor,
        user_input=user_input,
        thread_id=str(request.thread_id),
    )

    # Return the response.
    return answers


